export default function Destination() {
  const [searchTerm, setSearchTerm] = useState("");

  const filteredDestinations = destinations.filter(place =>
    place.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="destination-container">
      <h1>Explore Amazing Places</h1>

      {/* Search Bar */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search for a place..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Destination Grid */}
      <div className="destination-grid">
        {filteredDestinations.length > 0 ? (
          filteredDestinations.map(place => (
            <div key={place.id} className="destination-card">
              <Link to={`/destination/${place.id}`} className="destination-image-link">
                <img src={place.image} alt={place.title} className="destination-image" />
              </Link>
              <h2 className="destination-title">{place.title}</h2>
              <p className="destination-description">{place.description}</p>
              <Link to={`/destination/${place.id}`} className="explore-btn">Explore Now</Link>
            </div>
          ))
        ) : (
          <p className="no-results">No places found.</p>
        )}
      </div>
    </div>
  );
}
